import React, { Component ,Redirect} from "react";
import axios from 'axios';

export default class Login extends Component {
    constructor() {
        super();
        this.state = {
          email : '',
          password:'',
          message:'',
          loggedin:false
        }
        const { history } = useHistory();
    }
    changeInputValue(event){
        // Cập nhật state
       this.setState({
         [event.target.id]: event.target.value
       })
    }
   submitForm(event) {
    event.preventDefault()
    const form={
        email: this.state.email,
        password:this.state.password
    }
    console.log(form);
    axios.post(`http://localhost:8081/login`,form)
      .then(res => {
            console.log(res.data);
            if(res.data.code===0){
                console.log(this.state)
               this.setState({
                   message:res.data.message
               })
            }else{
                // this.props.onChangeEmail(this.state.email);

                // this.props.history.push('/dashboard');
                this.setState({loggedin:true})
            }
      })
      .catch(error => console.log(error));
    }
    render() {
        if(this.state.loggedin)return (<Redirect push to="/dashboard" />);
        return (
            <div className="auth-wrapper">
            <div className="auth-inner">
            <form onSubmit={(event) => {
                this.submitForm(event)
                }}>
                <h3>Login</h3>

                <div style={{marginTop:"20px"}} className="form-group">
                    <input required type="email" id="email" onChange={(event) => this.changeInputValue(event)} className="form-control" placeholder="Enter email" />
                </div>

                <div style={{marginTop:"20px"}} className="form-group">
                    <input required type="password" id="password" onChange={(event) => this.changeInputValue(event)} className="form-control" placeholder="Enter password" />
                </div>
                <div style={{display:"flex",justifyContent:"center",marginTop:"20px"}} className="buttonbar">
                    <button type="submit" className="btn btn-primary btn-block">Submit</button>
                </div>
                <div style={{display:"flex",justifyContent:"center",marginTop:"20px"}}>
                    <div>{this.state.message}</div>
                </div>
                    </form>
                </div>
            </div>
        );
    }
}
