import React, { Component } from "react";

export default class Login extends Component {
    constructor() {
        super();
        this.state = {
          email : '',
          password:''
        }
    }
    changeInputValue(event){
        // Cập nhật state
       this.setState({
         [event.target.id]: event.target.value
       })
   }
    render() {
        return (
            <form onSubmit={(event) => {
                this.submitForm(event)
        }}>
                <h3>Login</h3>

                <div className="form-group">
                    <label>Email</label>
                    <input type="email" id="email" onChange={(event) => this.changeInputValue(event)} className="form-control" placeholder="Enter email" />
                </div>

                <div className="form-group">
                    <label>Password</label>
                    <input type="password" id="password" onChange={(event) => this.changeInputValue(event)} className="form-control" placeholder="Enter password" />
                </div>

                <button type="submit" className="btn btn-primary btn-block">Submit</button>

            </form>
        );
    }
}
