import React, { Component } from "react";
import axios from 'axios';
export default class Login extends Component {
    constructor() {
        super();
        this.state = {
          email : '',
          password:'',
          messages:[]
        }
    }
    changeInputValue(event){
        // Cập nhật state
       this.setState({
         [event.target.id]: event.target.value
       })
   }
   submitForm(event) {
    event.preventDefault()
    const form={
        email: this.state.email,
        password:this.state.password
    }
    console.log(form);
    axios.post(`http://localhost:8081/login`,form)
      .then(res => {
          console.log(res.data);
            if(res.data['code']==0){
                this.state.messages=[];
                this.state.messages.push(res.data.message);
            }
      })
      .catch(error => console.log(error));
  }
    render() {
        return (
            <form onSubmit={(event) => {
                this.submitForm(event)
                }}>
                <h3>Login</h3>

                <div className="form-group">
                    <label>Email</label>
                    <input type="email" id="email" onChange={(event) => this.changeInputValue(event)} className="form-control" placeholder="Enter email" />
                </div>

                <div className="form-group">
                    <label>Password</label>
                    <input type="password" id="password" onChange={(event) => this.changeInputValue(event)} className="form-control" placeholder="Enter password" />
                </div>

                <button type="submit" className="btn btn-primary btn-block">Submit</button>
                { this.state.messages.map(message => <div>{message}</div>)}
            </form>

        );
    }
}
